    private void btnIngresarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnIngresarMouseClicked
     
        
        lista.add(
            txtNombre.getText()+" "+
            txtIdCuerpo.getText()+" "+
            txtMunicipio.getText()+" "+
            txtTipoCuerpo.getText()+" "+
            txtTipoAgua.getText()+" "+
            txtIRCA.getText()+" "+
            txtDensidad.getText()
        );
        
        filas = new DefaultListModel();
        filas.addElement(lista);
        listaAdicionar.setModel(filas);
        
        GestionAnalisis.CrearCuerposAgua(
                txtTipoCuerpo.getText(), 
                txtTipoAgua.getText(), 
                txtIRCA.getText(),
                txtNombre.getText(), 
                txtIdCuerpo.getText(), 
                txtMunicipio.getText(), 
                txtDensidad.getText());
      
        limpiar ();
    
        
    }//GEN-LAST:event_btnIngresarMouseClicked

    private void btnProcesarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnProcesarMouseClicked

        filas = new DefaultListModel();
        filas.addElement(GestionAnalisis.calculoNivel());
        listaResultado.setModel(filas);
    }//GEN-LAST:event_btnProcesarMouseClicked

