    public void limpiar(){
            
                txtTipoCuerpo.setText(null); 
                txtTipoAgua.setText(null);
                txtIRCA.setText(null);
                txtNombre.setText(null); 
                txtIdCuerpo.setText(null); 
                txtMunicipio.setText(null); 
                txtDensidad.setText(null);
                
                txtIdCuerpo.requestFocus();
        }
    
    
    
